    private void lineToCommaBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lineToCommaBtnActionPerformed

        String lineString = lineArea.getText();
        String[] lines = lineString.split("\\n");

        StringBuilder sb = new StringBuilder();
        for (String line : lines) {

            String item = line.trim().replace("\\n", "");
            sb.append(item).append(",");
        }

        commaArea.setText(sb.toString());
    }//GEN-LAST:event_lineToCommaBtnActionPerformed

