    private void commaToLineActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_commaToLineActionPerformed

        String commaText = commaArea.getText();
        String[] itemList = commaText.split("\\,");

        String result = String.join("\n", itemList);
        
        lineArea.setText(result);
    }//GEN-LAST:event_commaToLineActionPerformed

